import React from 'react';
import ReactDOM from 'react-dom';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Login from "./components/login/login.js";
import SignUp from "./components/login/register.js";
import Home from "./components/dashboard/home.js";
import Background from './assets/wall4.jpg';
import Logo from './assets/edLogo.png';
import Reset from "./components/login/PasswordReset.js";
import ChangePassword from "./components/login/ChangePassword.js";
import Conf from "./components/login/confirmation.js";

//ReactDOM.render(<Routes />, document.getElementById('root'));



function App() {
  return (<Router>
    <div className="App">
      <nav className="navbar navbar-expand-sm navbar-light fixed-top">
          <img style={{width: '40px', height: '40px'}} src={Logo} alt="edLogo"/>
          <Link className="navbar-brand" to={"/sign-in"}>EduForm</Link>
          <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo02" aria-controls="navbarTogglerDemo02" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarTogglerDemo02">
            <ul className="navbar-nav ml-auto">
              <li className="nav-item">
                <Link className="nav-link" to={"/sign-in"}>Login</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to={"/sign-up"}>Sign up</Link>
              </li>
            </ul>
          </div>
      </nav>

      <div className="auth-wrapper">
        <div className="auth-inner">
          <Switch>
            <Route exact path='/' component={Login} />
            <Route path="/sign-in" component={Login} />
            <Route path="/sign-up" component={SignUp} />
            <Route path="/PasswordReset" component={Reset} />
            <Route path="/confirmation" component={Conf} />
            <Route path="/home" component={Home} />
            <Route path="/ChangePassword" component={ChangePassword} />

          </Switch>
        </div>
      </div>
    </div></Router>
  );
}

export default App;
