import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import googleBtn from '../../assets/btn_google_signin.png';
import Bootstrap from '../../.././node_modules/bootstrap/dist/css/bootstrap.min.css';
import Logo from '../.././assets/edLogo.png';
import PwReset from './PasswordReset';
import FacebookBtn from '../../assets/facebook.png';

const validateForm = (errors) => {
    let valid = true
    Object.values(errors).forEach(
    // if we have an error string, set valid to false
    (val) => val.length > 0 && (valid = false)
    );
    return valid;
}

const countErrors = (errors) => {
    let count = 0;
    Object.values(errors).forEach(
      (val) => val.length > 0 && (count = count+1)
    );
    return count;
}


class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            email: null,
            password: null,
            formValid: false,
            errorCount: 0,
            errors:{
                email: '',
                password: '',
            }
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    initialize = () => {
        this.state.email = null;
        this.state.password = null;
    }

    handleSubmit = (event) => {
        event.preventDefault();

        let vars = ["email","password"];

        var i;
        for (i = 0; i < vars.length; i++) {

            if(document.getElementById(vars[i]).value.length === 0){
                this.state.errors[vars[i]] = "Required";
            }

        }

        this.setState({formValid: validateForm(this.state.errors)});
        this.setState({errorCount: countErrors(this.state.errors)});


        if (this.state.errorCount === 0 && this.state.formValid){

            //alert("You are logged in!");
            console.log('Logged In');

            //return <Redirect to = "./sign-up" />

            return this.props.history.push('./home');

            //Initialize variables
            //initialize()
        }
    }

    handleChange = (event) => {

        event.preventDefault();


        this.state.errors["email"] = "";
        this.state.errors["password"] = "";

        const { name, value } = event.target;

        this.state[name] = value

    }


    googleSignIn = () => {
        console.log('Click!!!!');
     }

    render() {
        const {errors} = this.state;
        return (
          <div className="container">
            <div className="row myIntro">
              <div className="col-lg-5 col-md-7 bg-black myLogIn text-primarys">
                <form onSubmit={this.handleSubmit} noValidate>
                    <img style={{width: '70px', height: '70px'}} src={Logo} alt="edLogo"/>
                    <h1 style={{paddingBottom: '10px'}}> EduForm </h1>

                    <div className="form-group">
                        <input type= "email" id="email" name="email" className="form-control" placeholder="Enter email" style={{fontSize: '.9rem'}} onChange={this.handleChange} noValidate/>
                        {errors.email.length > 0 && <span className='error'>{errors.email}</span>}
                    </div>

                    <div className="form-group">
                        <input type= "password" id="password" name="password" className="form-control" placeholder="Enter password" style={{fontSize: '.9rem'}} onChange={this.handleChange} noValidate/>
                        {errors.password.length > 0 && <span className='error'>{errors.password}</span>}
                    </div>

                    <div className="form-group">
                        <div className="custom-control custom-checkbox">
                            <input type="checkbox" className="custom-control-input" id="customCheck1"/>
                            <label style={{fontSize: '1rem', fontWeight: 'bold'}} className="custom-control-label text-primarys" htmlFor="customCheck1">Remember me</label>
                        </div>
                    </div>

                    <button type="submit" className="btn btn-block log" style={{fontSize: '1.2rem', backgroundColor: '#febf63'}}>Log In</button>
                    <p style={{fontSize: '1rem', fontWeight: 'bold'}} className="forgot-password text-right">
                        <a href="/PasswordReset" style={{color: 'white'}}>Forgot password?</a>
                    </p>

                    <p style={{fontSize: '1rem', fontWeight: 'bold'}} className="other-sign-in text-center text-primarys">
                        or you can sign in with
                    </p>

                        <div className="row">
                          <div className="col google-signin">
                              <img style={{paddingTop: '18px'}} alt='google' src={googleBtn} className="googleBtn" onClick={this.googleSignIn} />
                          </div>

                          <div classname="col faceBook-signin">
                                <img style={{width: '200px', height: '85px'}} alt='google' src={FacebookBtn} className="googleBtn" onClick={this.googleSignIn} />
                          </div>
                        </div>




                </form>
                </div>
                </div>
            </div>

        );
    }

// Useful links for forms:
//https://react-bootstrap.github.io/components/forms/
//https://reactjs.org/docs/forms.html
}


export default withRouter(Login);
